package findthedifference

import "testing"

func TestFindTheDifference(t *testing.T) {
	tests := []struct {
		s        string
		t        string
		expected byte
	}{
		// Basic case where one letter 'e' is added.
		{s: "abcd", t: "abcde", expected: 'e'},

		// Single letter added to an empty string.
		{s: "", t: "y", expected: 'y'},

		// Adding letter 'f' to a shuffled version.
		{s: "abcde", t: "abfcde", expected: 'f'},

		// Edge case with both strings being empty.
		{s: "", t: "", expected: 0}, // Implementation-specific: may define behavior for this case.

		// Adding a duplicate letter that was not present.
		{s: "xxyyz", t: "xxyzzy", expected: 'z'},

		// Adding beginning letter of the alphabet.
		{s: "mnopqrstuvwxyz", t: "mnopqrstuvwxyzq", expected: 'q'},

		// Adding at the start and verifying order doesn't matter.
		{s: "stuv", t: "xstuv", expected: 'x'},

		// Large strings with an additional letter, for performance benchmarking.
		{s: "aaaaaaaaaa", t: "aaaaaaaaaab", expected: 'b'},

		// Punctuation addition in non-alphabet strings.
		{s: "12345", t: "123456", expected: '6'},

		{s: "", t: "", expected: 't'},
	}

	for _, tt := range tests {
		result := findTheDifference(tt.s, tt.t)
		if result != tt.expected {
			t.Errorf("findTheDifference(%q, %q) = %q; expected %q", tt.s, tt.t, result, tt.expected)
		}
	}
}
